package com.example.funfactsbikinibottomimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport android.widget.Toastimport androidx.recyclerview.widget.RecyclerViewclass ListCharacterAdapter(private val listCharacter: ArrayList<Character>, private val isGridMode: Boolean): RecyclerView.Adapter<ListCharacterAdapter.ListViewHolder>() {    private lateinit var onItemClickCallback: OnItemClickCallback    fun setOnItemClickCallback(onItemClickCallback: OnItemClickCallback) {        this.onItemClickCallback = onItemClickCallback    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ListViewHolder {        // layout        val layoutId = if (isGridMode) {            R.layout.item_grid_character        } else {            R.layout.item_row_character        }        val view: View = LayoutInflater.from(parent.context).inflate(layoutId, parent, false)        return ListViewHolder(view)    }    override fun onBindViewHolder(holder: ListViewHolder, position: Int) {        val (name, funfact, birthday, job, description, photo) = listCharacter[position]        holder.tvName.text = name        holder.tvJob.text = job        holder.ivPhoto.setImageResource(photo)        holder.itemView.setOnClickListener {            onItemClickCallback.onItemClicked(listCharacter[holder.adapterPosition])            val intent = Intent(holder.itemView.context, DetailActivity::class.java)            intent.putExtra(DetailActivity.EXTRA_CHARACTER, listCharacter[holder.adapterPosition])            holder.itemView.context.startActivity(intent)        }    }    override fun getItemCount(): Int = listCharacter.size    // list    class ListViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val tvName: TextView = itemView.findViewById(R.id.tv_item_name)        val tvJob: TextView = itemView.findViewById(R.id.tv_item_job)        val ivPhoto: ImageView = itemView.findViewById(R.id.img_item_photo)    }    interface OnItemClickCallback {        fun onItemClicked(data: Character)    }}